    self.map_h = #self.map -- Obtains the height of the map
    self.map_x = 0
    self.map_y = 0
    self.map_display_buffer = 2 -- We have to buffer one tile before and behind our viewpoint.
                               -- Otherwise, the tiles will just pop into view, and we don't want that.
    self.map_display_w = 43
    self.map_display_h = 24
    self.tile_w = 32
	self.tile_h = 32

end


function Level01:update(map_x , map_y)
	self.map_x = map_x
	self.map_y = map_y	
    -- check boundaries. remove this section if you don't wish to be constrained to the map.
    if self.map_x < 0 then
        self.map_x = 0
    end
    if self.map_y < 0 then
        self.map_y = 0
    end  
    if self.map_x > self.map_w * self.tile_w - self.map_display_w * self.tile_w then
        self.map_x = self.map_w * self.tile_w - self.map_display_w * self.tile_w
    end 
    if self.map_y > self.map_h * self.tile_h - self.map_display_h * self.tile_h then
        self.map_y = self.map_h * self.tile_h - self.map_display_h * self.tile_h
    end
end

function Level01:getMap_x()
	return self.map_x
end

function Level01:getMap_y()
	return self.map_y
end

function Level01:draw()
	self.offset_x = self.map_x % self.tile_w
    self.offset_y = self.map_y % self.tile_h
    self.firstTile_x = math.floor(self.map_x / self.tile_w)
    self.firstTile_y = math.floor(self.map_y / self.tile_h)    
    for y=1, (self.map_display_h + self.map_display_buffer) do
        for x=1, (self.map_display_w + self.map_display_buffer) do
            -- Note that this condition block allows us to go beyond the edge of the map.
            if y+self.firstTile_y >= 1 and y+self.firstTile_y <= self.map_h
                and x+self.firstTile_x >= 1 and x+self.firstTile_x <= self.map_w
            then
                love.graphics.draw(
                    self.tile[self.map[y+self.firstTile_y][x+self.firstTile_x]], 
                    ((x-1)*self.tile_w) - self.offset_x - self.tile_w/2, 
                    ((y-1)*self.tile_h) - self.offset_y - self.tile_h/2)
            end
        end
    end
end
